        console.log(taskEdit.listQuestion);
        dispatch(updateTask(taskEdit)).then(()=>{
            dispatch(getTaskByTeacher({"idUser":taskEdit.creator,"idClass":taskEdit.cour}))
        });
    }
    const formik = useFormik({
      initialValues: {
        Title: qes.Title,
        Body: qes.Body,
        Writerq: { _id: ""+documentData._id },
        Filee: [],
      },
      validationSchema: yupSchema,
  
      onSubmit: async (values) => {
        try {
          
          if(Images.length !==0 )
         { values.Filee = Images;
          alert("images : here : "+values.Filee);
        }
        else{
          values.Filee = qes.Filee;
        }
          const res = await AddquestionsApi.putQuestions(values, qes._id);
        //  dispatch(fetchQuestions(currentClass._id));
          socket.emit("send_question", "message");
        } catch (error) {
          alert(error);
        }
      },
    });
    return (
        <div>
             <Form  onSubmit={handleUpdate}>
        <Form.Input
          label="Titre"
          type="text"
        
          name="Titre"
          maxLength="40"
          
          required
          value={taskEdit.title}
          onChange={(e) =>
            setTaskEdit({ ...taskEdit, title: e.target.value })
          }
        />
        <Form.TextArea
          label="Description"
          type="TextArea"
       
          name="Description"
          maxLength="5000"
          required
          value={taskEdit.description}
          onChange={(e) =>
            setTaskEdit({ ...taskEdit, description: e.target.value })
          }
        />
           <SemanticDatepicker
                    onChange={(e, data) =>
                        setTaskEdit({ ...taskEdit, endDate: data.value })
                    }
                    format={"YYYY-MM-DD"}
                    value={taskEdit.endDate|Date}
                   // value={taskEdit.endDate.format("YYYY-MM-DD")}
                  />
                     <FileUploadEdit
          refreshFunction={updateImages}
            listfile={props.task.listQuestion}  
            Enbale={enableUpload}
          />
        <Message
          success
          color="green"
          header="Nice one! ðŸ“’ ðŸ“• ðŸ“š ðŸ“–"
            
        />
        <Message
          warning
          color="yellow"
          header="Woah! ðŸ˜± ðŸ˜¨"
        
        />
        <Button color="green" floated="right">
          Save
        </Button>
        <br /> {/* Yikes! Deal with Semantic UI React! */}
      </Form>
        </div>
    )
}
